import { createRouter, createWebHistory } from 'vue-router';
import Home from '../views/HomeView.vue';
import Analysis from '../views/AnalysisView.vue';
import Prediction from '../views/PredictionView.vue';
import KnowledgeMap from '../views/KnowledgeMapView.vue';
import Statistics from '../views/StatisticsView.vue';
import Scrapy from '../views/ExhibitionView.vue';

const routes = [
  {
    path: '/',
    redirect: '/home',
  },
  {
    path: '/home',
    name: 'Home',
    component: Home,
  },
  {
    path: '/analysis',
    name: 'Analysis',
    component: Analysis,
  },
  {
    path: '/prediction',
    name: 'Prediction',
    component: Prediction,
  },
  {
    path: '/knowledgeMap',
    name: 'KnowledgeMap',
    component: KnowledgeMap,
  },
  {
    path: '/statistics',
    name: 'Statistics',
    component: Statistics,
  },
  {
    path: '/scrapy',
    name: 'Scrapy',
    component: Scrapy,
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
