export default {
  __name: 'PieChart',
  props: {
    data: {
      type: Array,
      required: true
    },
    xAxisDataKey: {
      type: String,
      default: 'date'
    },
    yAxisDataKey: {
      type: String,
      default: 'value'
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const data = ref([]);
    let baseUrl = 'http://47.120.41.97:8002/bugs/';
    const fetchData = async () => {
      try {
        const response = await axios.get(baseUrl + 'companies');
        data.value = response.data; // 假设后端返回的数据格式符合饼图需要的数据结构
        if (chart.value) {
