        }
        console.log(data);
        console.log(data.value);
        console.log(chart);
        console.log(chart.value);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    //initiate of pie chart
    const props = __props;
    const chartRef = ref(null);
    onMounted(() => {
      initChart();
    });
    watch(() => props.data, () => {
      if (chart) {
        chart.setOption(getOption());
      }
    }, {
      deep: true
    });
    let chart;
    const getOption = () => {
      return {
        title: {
          text: 'Referer of a Website',
          subtext: 'Fake Data',
          left: 'center'
        },
        tooltip: {
          trigger: 'item'
        },
        legend: {
          orient: 'vertical',
          left: 'left'
        },
        series: [{
          name: 'Access From',
          type: 'pie',
          radius: '75%',
          data: data.value.map(item => ({
            value: item.value,
            name: item.name
          })),
          // data: Object.entries(data).map(([name, value]) => ({ name, value })),
          emphasis: {
            itemStyle: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      };
    };
    const initChart = () => {
      fetchData();
      chart = echarts.init(chartRef.value);
      chart.setOption(getOption());
    };
    const __returned__ = {
      data,
      get baseUrl() {
        return baseUrl;
      },
      set baseUrl(v) {
        baseUrl = v;
      },
      fetchData,
      props,
      chartRef,
      get chart() {
        return chart;
      },
      set chart(v) {
        chart = v;
      },
      getOption,
      initChart,
      get echarts() {
        return echarts;
      },
      ref,
      onMounted,
      watch,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
