// store/index.js
import { createStore } from 'vuex';

const store = createStore({
  state: {
    exhibition_type: 'overview', // 默认类型
  },
  mutations: {
    SET_EXHIBITION_TYPE(state, type) {
      state.exhibition_type = type;
    },
  },
  actions: {
    updateExhibitionType({ commit }, type) {
      commit('SET_EXHIBITION_TYPE', type);
    },
  },
  getters: {
  },
  modules: {
  },
});

export default store;
