export default {
  __name: 'LeftBar',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const Tab1 = defineAsyncComponent(() => import('../components/LineChart.vue'));
    const Tab2 = defineAsyncComponent(() => import('../components/BarChart.vue'));
    const Tab3 = defineAsyncComponent(() => import('../components/PieChart.vue'));
    const Tab4 = defineAsyncComponent(() => import('../components/ScatterChart.vue'));
    const tabs = [{
      label: 'Bug数量随时间增长图',
      name: 'LineChart'
    }, {
      label: '状态-严重性图',
      name: 'BarChart'
    }, {
      label: '企业数量统计',
      name: 'PieChart'
    }, {
      label: 'Tab 2',
      name: 'ScatterChart'
    }];
    const activeName = ref('LineChart');
    const currentComponent = ref([]);

    // onMounted(() => {
    //     loadComponent(activeName.value);
    // });

    function loadComponent(componentName) {
      if (componentName === 'LineChart') {
        currentComponent.value = Tab1;
        activeName.value = 'LineChart';
      } else if (componentName === 'BarChart') {
        currentComponent.value = Tab2;
        activeName.value = 'BarChart';
      } else if (componentName === 'PieChart') {
        currentComponent.value = Tab3;
        activeName.value = 'PieChart';
      } else if (componentName === 'ScatterChart') {
        currentComponent.value = Tab4;
        activeName.value = 'ScatterChart';
      }
    }
    const __returned__ = {
      Tab1,
      Tab2,
      Tab3,
      Tab4,
      tabs,
      activeName,
      currentComponent,
      loadComponent,
      ref,
      defineAsyncComponent
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
